(20, 1, '2014-06-03 19:34:57', '2014-06-03 15:34:57', '<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Strict//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-strict.dtd">\r\n<html xmlns="http://www.w3.org/1999/xhtml">\r\n<head>\r\n<meta http-equiv="Content-Type" content="text/html; charset=UTF-8"/>\r\n<title>Metro</title>\r\n<link rel="stylesheet" href="css/main.css" type="text/css"/>\r\n<!--[if IE ]><link rel="stylesheet" href="css/css-ie.css" media="screen"><![endif]-->\r\n\r\n<script type=''text/javascript'' src=''js/jquery-1.7.1.min.js''></script>\r\n\r\n<script type="text/javascript" src="js/form.js"></script>\r\n<script type="text/javascript" src="js/custom-form-elements.js"></script>\r\n\r\n<script type="text/javascript" src="js/slides.min.jquery.js"></script>\r\n<script type=''text/javascript''>\r\n	jQuery(function(){\r\n            jQuery(''#slides'').slides({\r\n                play: 5000,\r\n                pause: 5000,\r\n                hoverPause: true\r\n            });\r\n        });\r\n\r\n</script>\r\n\r\n<script type="text/javascript" src="js/jquery.infieldlabel.min.js"></script>\r\n<script type="text/javascript" src="js/functions.js"></script>\r\n\r\n<link rel="stylesheet" type="text/css" media="all" href="css/calendar-brown.css" />\r\n<script type="text/javascript" src="js/calendar_stripped.js"></script>\r\n<script type="text/javascript" src="js/calendar-en.js"></script>\r\n<script type="text/javascript" src="js/calendar-setup_stripped.js"></script>\r\n\r\n</head>\r\n<body>\r\n	<div class="container">\r\n		<div class="header">\r\n            <a id="logo" href="#">Metro</a>\r\n            <ul class="nav">\r\n                <li><a href="#">HOME</a></li>\r\n                <li class="item-level"><a href="#">MENU</a>\r\n                    <div class="nav-in">\r\n                        <ul>\r\n                            <li><a href="#">Food</a></li>\r\n                            <li><a href="#">Drinks</a></li>\r\n                            <li><a href="#">Kids</a></li>\r\n                        </ul>\r\n                    </div>\r\n                </li>\r\n                <li><a href="#">FUNCTIIONS</a></li>\r\n                <li><a href="#">CONTACT</a></li>\r\n            </ul>\r\n		</div>\r\n		<div class="wrapper">\r\n			<div class="wrapper-in">\r\n                <div class="content">\r\n                    <h1>Contact</h1>\r\n                    <p><b>Email:</b> publican@metrohotel.com.au<br /> <b>Phone:</b> (03) 9328 4222<br /> <b>Fax:</b> (03) 9328 4288</p>\r\n                    <h3>OPENING TIMES</h3>\r\n                    <p><b>Kitchen:</b><br /> Mon – Thu 12pm-2pm, 6-9pm;<br /> Fri 12pm-3pm, 6pm-9pm<br /> Sat 6pm-9pm<br /> <b>Bar:</b> Daily 11.30am-late<br /> <b>Closed:</b> Sundays</p>\r\n                    <h3>ADDRESS</h3>\r\n                    <p>36-42 Courtney St,<br /> (Cnr Blackwood St)<br /> North Melbourne 3051<br /> Victoria</p>\r\n                    <div class="box-img-map">\r\n                        <a href="#"><img src="images/img-map.jpg" alt="" /></a>\r\n                    </div>\r\n                </div>\r\n                <div class="sidebar">\r\n                    <div class="block-sid">\r\n                        <div class="box-icons">\r\n                            <a href="#"><img src="images/icon-facebook.png" alt="" /></a>\r\n                            <a href="#"><img src="images/icon-twitter.png" alt="" /></a>\r\n                            <a href="#"><img src="images/icon-urbanspoon.png" alt="" /></a>\r\n                        </div>\r\n                        <div class="title-follow_us">Follow us</div>\r\n                    </div>\r\n                    <div class="block-sid">\r\n                        <div class="box-icons">\r\n                            <a href="#"><img src="images/icon-pdf.png" alt="" /></a>\r\n                        </div>\r\n                        <div class="title-functions_package">Functions package</div>\r\n                    </div>\r\n                    <div class="block-sid">\r\n                        <div class="title-make_a_reservation">Make a reservation</div>\r\n                        <form class="form-sid" action="" method="post">\r\n            				<div class="box-line">\r\n                                <p class="label"><label for="name">name</label><input id="name" type="text" value=""/></p>\r\n                                <p class="label"><label style="left: 28px;" for="date">date</label><input id="date" type="text" value=""/></p>\r\n                                \r\n                                <script type="text/javascript">\r\n									jQuery(document).ready(function(){\r\n										Calendar.setup\r\n										({ \r\n											ifFormat    : "%d-%m-%Y",\r\n											inputField  : "date", // ID of the input field\r\n											button      : "date"      // ID of the button,\r\n										});   \r\n									})\r\n								</script>\r\n                                \r\n                            </div>\r\n                            <div class="box-line">\r\n                                <p class="label"><label for="phone">phone</label><input id="phone" type="text" value=""/></p>\r\n                                <p class="label"><label style="left: 28px;" for="time">time</label><input id="time" type="text" value=""/></p>\r\n                            </div>\r\n                            <div class="box-line">\r\n                                <p class="label"><label for="email">email</label><input id="email" type="text" value=""/></p>\r\n                            </div>\r\n                            <div class="box-line">\r\n                                <p class="label"><label for="mes">Number of people and special<br /> requirements...</label><textarea id="mes" cols="10" rows="10"></textarea></p>\r\n                            </div>\r\n                            <label class="sub"><input type="submit" value="" /></label>\r\n            			</form>\r\n                    </div>\r\n                </div>\r\n            </div>\r\n		</div>\r\n		<div id="footer">\r\n            <div class="footer-in">\r\n                <p>&copy; Copyright 2012 <a href="#">Metropolitan Hotel</a> / <a href="#">Contact us</a> / Site by <a href="#">Red Design</a></p>\r\n            </div>\r\n        </div>\r\n	</div>\r\n</body>\r\n</html>\r\n', 'contact', '', 'inherit', 'open', 'open', '', '19-revision-v1', '', '', '2014-06-03 19:34:57', '2014-06-03 15:34:57', '', 19, 'http://localhost/19-revision-v1/', 0, 'revision', '', 0);
